module aoc_2016.day2

type Direction =
    | Up
    | Down
    | Left
    | Right

    static member GetDirectionFromLetter letter =
        match letter with
        | 'L' -> Left
        | 'R' -> Right
        | 'U' -> Up
        | 'D' -> Down
        | _ -> failwith "Invalid letter"

type Pos =
    { X: int
      Y: int }

    member this.Move direction =
        match direction with
        | Up -> { X = this.X; Y = this.Y - 1 }
        | Down -> { X = this.X; Y = this.Y + 1 }
        | Left -> { X = this.X - 1; Y = this.Y }
        | Right -> { X = this.X + 1; Y = this.Y }

let solve () =
    let inp =
        "



"

    let keypad = [| [| 1; 2; 3 |]
                    [| 4; 5; 6 |]
                    [| 7; 8; 9 |] |]

    let isWithinBounds pos =
        0 <= pos.X && pos.X < keypad[0].Length && 0 <= pos.Y && pos.Y < keypad.Length

    let r =
        inp.Split '\n'
        |> Seq.scan
            (fun currentPos line ->
                line.Trim()
                |> Seq.fold
                    (fun (pos: Pos) item ->
                        let newPos = pos.Move(Direction.GetDirectionFromLetter item)
                        if isWithinBounds newPos then newPos else pos)
                    currentPos)
            { X = 1; Y = 1 }

    for item in r |> Seq.skip 1 do
        printf $"%A{keypad[item.Y][item.X]}"

